package com.ulp.inmobiliariafpestchanker.ui.inmueble;

import androidx.lifecycle.Observer;
import androidx.lifecycle.ViewModel;
import androidx.lifecycle.ViewModelProvider;

import android.content.Context;
import android.content.Intent;
import android.content.SharedPreferences;
import android.graphics.Bitmap;
import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.navigation.Navigation;

import android.provider.MediaStore;
import android.util.Log;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.AdapterView;
import android.widget.ArrayAdapter;
import android.widget.Spinner;
import android.widget.TextView;
import android.widget.Toast;

import com.google.android.gms.common.server.converter.StringToIntConverter;
import com.ulp.inmobiliariafpestchanker.R;

import com.ulp.inmobiliariafpestchanker.databinding.FragmentInmuebleBinding;
import com.ulp.inmobiliariafpestchanker.databinding.FragmentAgregarInmuebleBinding;
import com.ulp.inmobiliariafpestchanker.modelo.Inmueble;
import com.ulp.inmobiliariafpestchanker.modelo.TipoInmueble;
import com.ulp.inmobiliariafpestchanker.modelo.Ubicacion;

import java.io.ByteArrayOutputStream;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.Base64;
import java.util.List;

public class Agregar_Inmueble extends Fragment {

    Spinner spinner;
    int tipoInm;
    String[] tipoinmueble;
    int[] indicetipoinmueble;
    private Inmueble i = new Inmueble();
    private AgregarInmuebleViewModel iViewModel;



    private FragmentAgregarInmuebleBinding binding;


    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container,
                             @Nullable Bundle savedInstanceState) {


        iViewModel = new ViewModelProvider(this).get(AgregarInmuebleViewModel.class);
        binding = FragmentAgregarInmuebleBinding.inflate(inflater, container, false);


        // Se setea una imagen por defecto vacia
        i.setImagen("");

            iViewModel.getUbicacionActual();
        iViewModel.obtenerUbicacionInmueble();


        binding.ivPropiedad.setOnClickListener(new View.OnClickListener() {



            @Override
            public void onClick(View v) {
                abrirCamara(v);
            }
        });



        binding.btnRegistrar.setOnClickListener(new View.OnClickListener() {

            @Override
            public void onClick(View v) {

                i.setDireccion(binding.etDireccion.getText().toString());
                i.setTipouso(binding.etTipoUso.getText().toString());
                i.setTipoinmuebleId(tipoInm);
                i.setCantidadambientes(Integer.valueOf(binding.etCantidadAmbientes.getText().toString()));
                i.setPrecioinmueble(Double.valueOf(binding.etPrecio.getText().toString()));
                i.setPropietario(0);
                i.setDisponible(binding.cbDisponible.isChecked());
                i.setCoordenadaE(Double.valueOf(binding.etCoordenadaE.getText().toString()));
                i.setCoordenadaN(Double.valueOf(binding.etCoordenadaN.getText().toString()));
                iViewModel.insertarInmueble(i);
            }
        });


        View root = binding.getRoot();
        iViewModel.obtenerTipoInmueble();


        iViewModel.getFoto().observe(getViewLifecycleOwner(), new Observer<Bitmap>() {
            @Override
                public void onChanged(Bitmap bitmap) {
                binding.ivPropiedad.setImageBitmap(bitmap);


                ByteArrayOutputStream baos=new ByteArrayOutputStream();
                bitmap.compress(Bitmap.CompressFormat.JPEG,100, baos);
                byte [] b=baos.toByteArray();

                String encoded = Base64.getEncoder().encodeToString(b);
                i.setImagen(encoded);

            }
        });

        iViewModel.getMutableLeerTipoInmueble().observe(getViewLifecycleOwner(), new Observer<Bundle>() {
            @Override
            public void onChanged(Bundle tipoinmuebleBundle) {

                spinner = root.findViewById(R.id.spTipoInmueble);
                Context context = root.getContext();

                tipoinmueble = new String[tipoinmuebleBundle.getStringArray("tipoinmueble").length];
                indicetipoinmueble = new int[tipoinmuebleBundle.getStringArray("tipoinmueble").length];


                tipoinmueble = tipoinmuebleBundle.getStringArray("tipoinmueble");
                indicetipoinmueble = tipoinmuebleBundle.getIntArray("indiceTipoInmueble");

                tipoInm = indicetipoinmueble[0];

                ArrayAdapter<String> adapter = new ArrayAdapter<String>(getActivity(), android.R.layout.simple_spinner_item, tipoinmueble);
                adapter.setDropDownViewResource(android.R.layout.simple_spinner_item);
                spinner.setAdapter(adapter);
                spinner.setOnItemSelectedListener(new AdapterView.OnItemSelectedListener() {
                    @Override
                    public void onItemSelected(AdapterView<?> parent, View view, int position, long id) {
                    tipoInm = indicetipoinmueble[position];

                    }

                    @Override
                    public void onNothingSelected(AdapterView<?> parent) {
                    }
                });
            }
        });


        iViewModel.getUbicacionActual().observe(getViewLifecycleOwner(), new Observer<Ubicacion>() {
            @Override
            public void onChanged(Ubicacion ubicacion) {

                    i.setCoordenadaE(Double.parseDouble(ubicacion.getLatitud().toString()));
                    i.setCoordenadaN(Double.parseDouble(ubicacion.getLongitud().toString()));
                    binding.etCoordenadaE.setText(i.getCoordenadaE()+" ");
                    binding.etCoordenadaN.setText(i.getCoordenadaN()+" ");
            }
        });


        return root;

    }

    public void abrirCamara(View v) {

        Intent takePictureIntent = new Intent(MediaStore.ACTION_IMAGE_CAPTURE);
        if (takePictureIntent.resolveActivity(getActivity().getPackageManager()) != null) {
            startActivityForResult(takePictureIntent, 1);
        }
    }

    @Override
    public void onActivityResult(int requestCode, int resultCode, @Nullable Intent data) {
        Agregar_Inmueble.super.onActivityResult(requestCode, resultCode, data);
        iViewModel.respuestaDeCamara(requestCode, resultCode, data, 1);
    }


    @Override
    public void onActivityCreated(@Nullable Bundle savedInstanceState) {
        super.onActivityCreated(savedInstanceState);

        // TODO: Use the ViewModel


    }

}



